<?phpnamespace App\Providers;use Illuminate\Support\ServiceProvider;use OpenAI;use OpenAI\Client;use OpenAI\Laravel\Exceptions\ApiKeyIsMissing;class OpenAIServiceProvider extends ServiceProvider{    public function register(): void    {        $this->app->singleton(Client::class, static function (): Client {            $apiKey = config('supra.openai.api_key');            $organization = config('supra.openai.organization');            if (! is_string($apiKey) || ($organization !== null && ! is_string($organization))) {                throw new \InvalidArgumentException(                    'The OpenAI API Key is missing. Please run `gpt configure`'                );            }            return OpenAI::client($apiKey, $organization);        });        $this->app->alias(Client::class, 'openai');    }    public function boot(): void    {    }}